import React, { Component } from "react";


class Main extends Component{

    state = {
        Heading: '',
        Description: '',
        Notes: [],
    }

    AddNote = (e) => {

        e.preventDefault();


        if(this.state.Heading === "" || this.state.Description === ""){
            alert("All fields are mandatory");
            return;
        }

        const newNotesList = [...this.state.Notes, this.state];

        this.setState({Notes: newNotesList});

        this.state.Heading = "";
        this.state.Description = "";

        console.log(this.state);

    }


    render() {
        return (
            <div className="ui main">
                <h2>Add Note</h2>
                <form className="ui form" onSubmit={this.AddNote}>
                    <div className="field">
                        <label>Heading</label>
                        <input type="text" name="Heading" placeholder="Heading" value={this.state.Heading} onChange={(e) => this.setState({Heading: e.target.value})} />
                    </div>
                    <div className="field">
                        <label>Description</label>
                        <input type="text" name="Description" placeholder="Description" value={this.state.Description} onChange={(e) => this.setState({Description: e.target.value})} />
                    </div>
                    <button className="ui button blue">Add</button>
                </form>
            </div>
        );
    }

}

export default Main;
