    //                 </div>
    //             </center>
    //         </div>
    //     );
    // }


    render() {
        return (
          <div className="container-fluid my-5">
            <div className="row">
              <div className="col-sm-6 mx-auto text-white shadow-lg p-3">
                <h2 className="text-center"> My Notes </h2>
                <div className="container-fluid">
                  <div className="row">
                    <div className="col-9">
                      <input type="text" className="form-control" placeholder="Heading" value={this.state.text} onChange={this.handleChange} />
                    </div>
                    <div className="col-2">
                      <button className="btn btn-warning px-5 font-weight-bold" onClick={this.handleAdd}>Add</button>
                    </div>
                  </div>
                  <div className="conatiner">
                    <ul className="list-unstyled row m-5">
                      {
                        this.state.items.map((value, i) => {
                          return <Plan key={i} id={i} value={value} sendData={this.handleDelete} />
                        })
                      }
                    </ul>
                    {/* <ul className="list-unstyled row m-5">
                      <Plan p={this.state.items} sendData={this.handleDelete} />
                    </ul> */}
                  </div>
                </div>
    
              </div>
            </div>
          </div>
        )
      }

}

export default Main;
