import React, { Component } from "react";
import DisplayNote from "./DisplayNote";
// import App from "./App";


class Main extends Component{

    state = {
        Heading: '',
        Description: '',
        Notes: [],
    }

    AddNote = (e) => {

        e.preventDefault();

        if(this.state.Heading === "" || this.state.Description === ""){
            alert("All fields are mandatory");
            return;
        }

        // this.props.AddNoteInNotes(this.state.Description);

        const newNotesList = [...this.state.Notes, this.state];

        this.setState({Notes: newNotesList});

        console.log(newNotesList);

        this.setState({Heading:"", Description:""});

        // console.log(this.state);

    }

    DeleteNote = (e) => {

        cons

        // console.log('Deleted');
    }

    changeHeading = (e) => {
        this.setState({
            Heading: e.target.value
        })
    }

    changeDescription = (e) => {
        this.setState({
            Description: e.target.value
        })
    }


    render() {
        return (
            <div className="ui main">
                <h2>Add Note</h2>
                <form className="ui form" onSubmit={this.AddNote}>
                    <div className="field">
                        <label>Heading</label>
                        <input type="text" name="Heading" placeholder="Heading" value={this.state.Heading} onChange={this.changeHeading} />
                    </div>
                    <div className="field">
                        <label>Description</label>
                        <input type="text" name="Description" placeholder="Description" value={this.state.Description} onChange={this.changeDescription} />
                    </div>
                    <button className="ui button blue">Add</button>
                </form>
                <center>
                    <div>
                {
                    this.state.Notes.map((value, idx) => {
                        return <DisplayNote value={value} key={idx} sendData={this.DeleteNote} />
                    })
                }
                    </div>
                </center>
            </div>
        );
    }

}

export default Main;
